            $0.entryPoint = entryPoint
            $0.facetRegistry = "0x9a95d201BB8F559771784D12c01F8084278c65E5"
            $0.defaultFallback = "0x522cDc7558b5f798dF5D61AB09B6D95Ebd342EF9"
