            $0.amount = Data(hexString: "0x0186a0")!
            $0.chainID = 1
            $0.idassetsID = 1
            $0.nonce = "0000000000000000".data(using: .utf8)!
            $0.remark = ""
            $0.balance = Data(hexString: "0x0f4240")!
