    input.set_private_key(key.data(), key.size());
    auto& erc721 = *input.mutable_transaction()->mutable_erc721_transfer();
    erc721.set_from(fromAddress);
    erc721.set_to(toAddress);
    erc721.set_token_id(tokenId.data(), tokenId.size());

    std::string expected = "";

