#include "TestUtilities.h"
#include <TrustWalletCore/TWEthereum.h>
#include <TrustWalletCore/TWHash.h>
#include <Ethereum/EIP1967.h>
#include <HexCoding.h>
#include <PrivateKey.h>

#include <gtest/gtest.h>

namespace TW::Ethereum::tests {

TEST(EthereumEip1967, Example0) {
    // C++
    {
        const std::string& login_address = "0x5C9eb5D6a6C2c1B3EFc52255C0b356f116f6f66D";
        const Data data = parse_hex("0xc4d66de8000000000000000000000000a5a1dddef094095afb7b6e322de72961df2e1988");
        const Data initCode = Ethereum::getEIP1967ProxyInitCode(login_address, data);
        ASSERT_EQ(hexEncoded(initCode), "");
    }
}

}
